INSERT INTO `llx_emailcollector_emailcollector` (`rowid`, `entity`, `ref`, `label`, `description`, `host`, `hostcharset`, `login`, `password`, `source_directory`, `target_directory`, `maxemailpercollect`, `datelastresult`, `codelastresult`, `lastresult`, `datelastok`, `note_public`, `note_private`, `date_creation`, `tms`, `fk_user_creat`, `fk_user_modif`, `import_key`, `status`) VALUES (1,1,'Collect_Ticket_Requets','Example to collect ticket requests','This collector will scan your mailbox to find emails that match some rules and create automatically a ticket (Module Ticket must be enabled) with the email informations. You can use this collector if you provide some support by email, so your ticket request will be automatically generated. If the collector Collect_Responses is also enabled, when you send an email from the ticket, you may also see answers of your customers or partners directly on the ticket view.',NULL,'UTF-8',NULL,NULL,'INBOX',NULL,100,NULL,NULL,NULL,NULL,NULL,NULL,'2022-02-20 13:16:16','2022-02-20 12:16:16',1,NULL,NULL,0),(2,1,'Collect_Responses_Out','Example to collect answers to emails done from your external email software','This collector will scan your mailbox \"Sent\" directory to find emails that was sent as an answer of another email directly from your email software and not from Dolibarr. If such an email is found, the event of answer is recorded into Dolibarr.',NULL,'UTF-8',NULL,NULL,'Sent',NULL,100,NULL,NULL,NULL,NULL,NULL,NULL,'2022-02-20 13:16:16','2022-02-20 12:16:16',1,NULL,NULL,0),(3,1,'Collect_Responses_In','Example to collect any received email that is a response of an email sent from Dolibarr','This collector will scan your mailbox to find all emails that are an answer of an email sent from your application. An event (Module Agenda must be enabled) with the email response will be recorded at the good place. For example, if your send a commercial proposal, order, invoice or message for a ticket by email from the application, and your customer answers your email, the system will automatically catch the answer and add it into your ERP.',NULL,'UTF-8',NULL,NULL,'INBOX',NULL,100,NULL,NULL,NULL,NULL,NULL,NULL,'2022-02-20 13:16:16','2022-02-20 12:16:16',1,NULL,NULL,0),(4,1,'Collect_Leads','Example to collect leads','This collector will scan your mailbox to find emails that match some rules and create automatically a lead (Module Project must be enabled) with the email informations. You can use this collector if you want to follow your lead using the module Project (1 lead = 1 project), so your leads will be automatically generated. If the collector Collect_Responses is also enabled, when you send an email from your leads, proposals or any other object, you may also see answers of your customers or partners directly on the application.<br>Note: With this initial example, the title of the lead is generated including the email. If the thirdparty can\'t be found in database (new customer), the lead will be attached to the thirdparty with ID 1.',NULL,'UTF-8',NULL,NULL,'INBOX',NULL,100,NULL,NULL,NULL,NULL,NULL,NULL,'2022-02-20 13:16:16','2022-02-20 12:16:16',1,NULL,NULL,0),(5,1,'Collect_Candidatures','Example to collect email for job candidatures','This collector will scan your mailbox to find emails send for a recruitment (Module Recruitment must be enabled). You can complete this collector if you want to automaticallycreate a candidature for a job request.Note: With this initial example, the title of the candidature is generated including the email.',NULL,'UTF-8',NULL,NULL,'INBOX',NULL,100,NULL,NULL,NULL,NULL,NULL,NULL,'2022-02-20 13:16:16','2022-02-20 12:16:16',1,NULL,NULL,0);
